import NewMeetupForm from "../components/meetups/NewMeetupForm";
import { useQuery } from 'react-query';

const MEETUPS_QUERY = `
{
    meetups_meetup {
        address
        description
        id
        image
        title
      }    
}
`;

function NewMeetupPage() {

    async function testGraphql() {
        const response = await fetch('http://localhost:8080/v1/graphql', {
           method:'POST',
           headers:{'content-type':'application/json'},
           body:JSON.stringify({query: MEETUPS_QUERY})
        })

        const rsponseBody = await response.json();
        return rsponseBody.data;

     }

    async function addMeetupHandler(meetupData) {
        const response = await fetch('http://127.0.0.1:8000/meetups/add',
        {   headers: {'Content-Type': 'application/json'},
            method: 'POST',
            body: JSON.stringify(meetupData),
        });
    }

    async function addMeetupGraphql(meetupData) {
        const TEST_ADD_QUERY = `
        mutation MyQuery {
            insert_meetups_meetup_one(object: 
              {address: "Test", 
               description: "Test",
               image: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/17/GraphQL_Logo.svg/1024px-GraphQL_Logo.svg.png",
               title: "This was inserted with GraphQL!"}) {
              id
            }
          }          
        `
        const response = await fetch('http://localhost:4300/graphql', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                query: TEST_ADD_QUERY,
            }),
        })
        const responseBody = await response.json();
        console.log("Results of POST attempt:" + responseBody);


    }

    return <section>
         <h1>Add New Meetup</h1>
        <NewMeetupForm onAddMeetup={addMeetupHandler}/>
        <button onClick={addMeetupGraphql}>Test graphql</button>
    </section>

}

export default NewMeetupPage;
